INSERT INTO `news` (`id`, `title`, `content`, `picture_id`, `pictures`, `user_id`, `status_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, NULL, '<p>چرا شهرداری مجبور به فروش دارایی&zwnj;های خود شد</p>', 0, '', 1, 1, '2019-04-13 07:33:47', '2019-04-14 02:31:06', '2019-04-14 02:31:06'),
(2, 'چرا شهرداری مجبور به فروش دارایی‌های خود شد', '<p dir=\"rtl\">شهر بزرگ و پرهزینه تهران نیازمند طرحی نو در اداره شهر است. نگرانی از وضعیت اقتصادی پایتخت و نحوه اداره آن در سال جدید را می&zwnj;توان در صحبت&zwnj;هایی که شهردار تهران در جمع مدیران شهری بیان کرده است، به&zwnj;خوبی فهمید.</p>\r\n<p dir=\"rtl\"><strong>بیشتر بخوانید:</strong></p>\r\n<ul dir=\"rtl\">\r\n<li><a href=\"https://mag.nilsoo.com/3372-2/\">باغ&zwnj;های تهران همچنان به مسلخ می&zwnj;روند</a></li>\r\n<li><a href=\"https://mag.nilsoo.com/%D8%B4%D9%87%D8%B1%D8%AF%D8%A7%D8%B1%DB%8C%E2%80%8C%D9%87%D8%A7-%D8%AF%D8%B1-%DA%86%D9%86%D8%A8%D8%B1%DB%80-%D9%85%D8%B9%DB%8C%D9%88%D8%A8-%D8%AF%D8%B1%D8%A2%D9%85%D8%AF%D9%87%D8%A7%DB%8C-%D9%86%D8%A7/\">شهرداری&zwnj;ها در چنبرۀ معیوب درآمدهای ناپایدار</a></li>\r\n</ul>\r\n<p dir=\"rtl\">به نظر می&zwnj;رسد پیروز حناچی کار سختی پیش&zwnj;رو دارد، رشد تورم، مشکلات اقتصادی، کاهش درآمد پایدار شهردار&zwnj;ی&zwnj;ها و&hellip; از جمله موانع بزرگ بر سر اداره شهر تهران که وی به آن&zwnj;ها اشاره کرده است.</p>\r\n<p dir=\"rtl\">راهکار او انجام برنامه&zwnj;ریزی برای همه امور اداره شهر با بررسی عملکرد مالی شهرداری تهران در یک دهه اخیر است. آن&zwnj;طور که پیروز حناچی عنوان کرده است؛ در سال ۹۱ که ۸۰ درصد از درآمد شهرداری به ساخت&zwnj;وساز وابسته بود پیش&zwnj;بینی می&zwnj;شد که این سطح از ساخت&zwnj;وساز و چنین درآمدی ادامه نخواهد یافت و شهرداری در سال&zwnj;های بعد به مشکل خواهد خورد. این در حالی است که در شرایط کنونی نیز به&zwnj;رغم درآمدهایی که شهرداری تهران دارد، اما کفاف تأمین هزینه&zwnj;های طرح&zwnj;ها و برنامه&zwnj;های این مجموعه را نمی&zwnj;د&zwnj;هد.</p>\r\n<p dir=\"rtl\">تا به امروز حدود ۷۰ تا ۸۰ درصد درآمد شهرداری&zwnj;های کلان&zwnj;شهرها از فروش تراکم و عوارض، صدور پروانه ساختمان تأمین شده است.</p>\r\n<p dir=\"rtl\">بر اساس این گزارش، سیاست کاهش کمک&zwnj;های دولتی که بدون درنظرگرفتن منابع درآمدی جایگزین صورت گرفت مشکلات عدیده&zwnj;ای را برای شهرداری&zwnj;ها به وجود آورده است؛ به&zwnj;طوری&zwnj;که تا به امروز حدود ۷۰ تا ۸۰ درصد درآمد شهرداری&zwnj;های کلان&zwnj;شهرها از فروش تراکم و عوارض، صدور پروانه ساختمان تأمین شده است. در نتیجه با اجرای سیاست خودکفایی شهرداری&zwnj;ها بدون پیش&zwnj;بینی منابع درآمدی جایگزین بخش&zwnj;های درآمدی شهرداری&zwnj;ها را در وضعیت ناپایداری قرار داد. حال&zwnj;آنکه شهرداری برای خدما&zwnj;ت&zwnj;رسانی مطلوب در اداره شهر نیازمند تأمین منابع مالی پایدار است این مهم تا به امروز با روش فروش تراکم و دیگر درآمدهای ناپایدار انجام شده است.</p>\r\n<h4 dir=\"rtl\"><strong>فروش املاک</strong><strong>، تدبیر شهردار برای تأمین مالی</strong></h4>\r\n<figure class=\"wp-block-image\" dir=\"rtl\"><img class=\"wp-image-3631\" src=\"https://mag.nilsoo.com/wp-content/uploads/2019/04/20190304111231541-1-1024x682.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://mag.nilsoo.com/wp-content/uploads/2019/04/20190304111231541-1.jpg 1024w, https://mag.nilsoo.com/wp-content/uploads/2019/04/20190304111231541-1-300x200.jpg 300w, https://mag.nilsoo.com/wp-content/uploads/2019/04/20190304111231541-1-768x511.jpg 768w\" alt=\"\" /></figure>\r\n<p dir=\"rtl\">بااین&zwnj;حال، روزهای گذشته شهرداری تهران برای تأمین مالی ۲۰ درصد از هزینه&zwnj;های پایتخت در سال ۹۸، لیست فروش ۵۹۵ فقره از املاک تحت مالکیت خود را منتشر کرد.</p>\r\n<p dir=\"rtl\">بر اساس این گزارش، از مجموع بودجه ۱۸ هزار و ۹۰۰ میلیارد تومانی اداره پایتخت در سال جاری، رقمی معادل ۳هزار و ۸۰۰ میلیارد تومان از محل منابع غیرنقدی ناشی از &laquo;فروش ملک&raquo; و &laquo;صدور جواز ساخت بدون دریافت پول نقد (هولوگرام)&raquo; تأمین می&zwnj;شود.</p>\r\n<p dir=\"rtl\">در همین راستا، شهرداری لیست اموال قابل&zwnj;فروش برای سال ۹۸ را اعلام عمومی کرده است. این لیست مجموعه&zwnj;ای از املاک و مستغلاتی است که مالکیت آن به لحاظ ثبتی و رسمی در اختیار شهرداری تهران قرار دارد.</p>\r\n<p dir=\"rtl\">طبق آنچه در جزئیات ۵۹۵ فقره املاک و مستغلات قابل&zwnj;فروش قابل&zwnj;مشاهده است: ۹۸ درصد این املاک در نیمه جنوبی پایتخت و بیش از ۵۰ درصد آن در جنوبی&zwnj;ترین نقطه حریم پایتخت قرار دارند. این املاک فوق مرغوب نیستند و می&zwnj;توان گفت املاکی هستند که از نظر مدیریت شهری پایتخت امکان بهره&zwnj;برداری و انتفاع حداکثری از آن&zwnj;ها برای مدیریت شهری وجود ندارد که البته این موضوع به معنای قابل استفاده نبودن این املاک برای سایر بخش&zwnj;ها از جمله بخش خصوصی نیست.</p>\r\n<p dir=\"rtl\">نحوه فروش املاک معرفی&zwnj;شده، مزایده&zwnj;ای است و شامل انواع زمین، پارکینگ، پاساژ، ساختمان اداری و تعداد معدودی آپارتمان مسکونی است. نکته جالب آنکه ۵۹۵ فقره ملک معرفی&zwnj;شده در این لیست حدود ۱۰ درصد از کل موجودی اعلام&zwnj;شده از املاک قابل&zwnj;فروش تحت مالکیت شهرداری تهران است.</p>\r\n<p dir=\"rtl\">از مجموع بودجه ۱۸ هزار و ۹۰۰ میلیارد تومانی اداره پایتخت در سال جاری، رقمی معادل ۳ هزار و ۸۰۰ میلیارد تومان از محل منابع غیرنقدی ناشی از &laquo;فروش ملک&raquo; و &laquo;صدور جواز ساخت بدون دریافت پول نقد (هولوگرام)&raquo; تأمین می&zwnj;شود.</p>\r\n<p dir=\"rtl\">طبق آخرین گزارش رسمی شهرداری مربوط به ابتدای سال ۹۷، موجودی املاک قابل&zwnj;فروش و غیرقابل فروش (همچون ساختمان&zwnj;های اداری) تحت مالکیت شهرداری تهران بیش از ۶۰هزار فقره هستند که از این میان ۶هزار و ۲۹۸ فقره در گروه املاک قابل&zwnj;فروش قرار دارند. از این تعداد، ۲۵ درصد املاک معادل یک&zwnj;هزار و ۵۵۲ فقره دارای کاربری مسکونی، ۷/ ۷ درصد املاک معادل ۴۸۶ فقره دارای کاربری اداری، ۲۰ درصد املاک معادل یک&zwnj;هزار و ۲۷۸ فقره دارای کاربری تجاری و ۱۳ درصد آن&zwnj;ها معادل ۸۱۸ قطعه، زمین است. مطابق با برآوردهای کارشناسی انجام&zwnj;شده، مجموع ارزش ۵۹۵ ملک ارائه&zwnj;شده در این لیست معادل ۶هزار و ۶۷۵ میلیارد تومان است که علی&zwnj;القاعده باید به شکل مزایده به فروش برسد. در این لیست نیز ۴۲ درصد املاک کاربری تجاری، ۲۹ درصد کاربری پارکینگ، ۱۰ درصد زمین و ۳ درصد آپارتمان با کاربری مسکونی هستند.</p>\r\n<p dir=\"rtl\">نکته قابل&zwnj;توجه در لیست املاک منتشرشده شهرداری این است که بخش عمده یعنی ۹۸ درصد املاک در نیمه جنوبی شهر واقع شده&zwnj; و برای فروش عرضه شده&zwnj;اند و بخش عمده املاک متعلق به شهرداری که در مناطق شمالی بوده و جزو املاک فوق مرغوب محسوب می&zwnj;شوند، عرضه نشده است.</p>\r\n<figure class=\"wp-block-image\" dir=\"rtl\"><img class=\"wp-image-3628\" src=\"https://mag.nilsoo.com/wp-content/uploads/2019/04/464200_146-1024x745.jpg\" sizes=\"(max-width: 1024px) 100vw, 1024px\" srcset=\"https://mag.nilsoo.com/wp-content/uploads/2019/04/464200_146.jpg 1024w, https://mag.nilsoo.com/wp-content/uploads/2019/04/464200_146-300x218.jpg 300w, https://mag.nilsoo.com/wp-content/uploads/2019/04/464200_146-768x559.jpg 768w\" alt=\"\" /></figure>\r\n<p dir=\"rtl\">بر اساس این گزارش، با وجود ادامه رکود بخش مسکن، زنگ هشدار شرایط سخت&zwnj;تر برای تأمین مالی اداره شهر نسبت&zwnj;به سال گذشته که پیش&zwnj;تر به&zwnj;واسطه تأمین منابع از محل صدور پروانه ساختمانی برای شهرداری تهران تأمین می&zwnj;شد به صدا درآمده است و هم&zwnj;اکنون چالش عدم کسب درآمد از محل صدور پروانه ساختمانی و تغییر کاربری برای شهرداری یک بحران است.</p>\r\n<p dir=\"rtl\">این راهکارها وضعیت اداره شهر را در درازمدت تأمین نخواهند کرد؛ چنانچه نا&zwnj;کارآمدی درآمدی و نظام مدیریت مالی، ناپایداری درآمد&zwnj;ها بی&zwnj;توجهی به برنامه&zwnj;ریزی حتی پس از تصویب قانون شهرداری&zwnj;ها تصویر روشنی از درآمد شهرداری&zwnj;ها و تأمین منابع درآمدی پروژه&zwnj;های شهری قرار نداده است.</p>\r\n<p dir=\"rtl\">در این راستا، کارشناسان شهری بر این باورند که در کنار راهکارهایی که شهردار پایتخت به مدیران شهری پیشنهاد می&zwnj;دهد، اگر دو لایحه مهم درآمدهای پایدار و مالیات بر ارزش&zwnj;افزوده در مجلس تصویب شود، بسیاری از مشکلات مالی شهرداری تهران حل خواهد شد و اگر هم تصویب نشود، مدیران شهری مجبور به اتخاذ روش&zwnj;هایی برای تأمین بودجه موردنیاز خود هستند.</p>', 0, '4,', 1, 2, '2019-04-13 07:35:26', '2019-04-15 00:51:07', NULL),
(3, 'چرا شهرداری مجبور به فروش دارایی‌های خود شد', '<p dir=\"rtl\">به گزارش &laquo;دنیای اقتصاد&raquo; از مجموع بودجه ۱۸ هزار و ۹۰۰ میلیارد تومانی&nbsp;<strong><a title=\"بودجه\" href=\"https://www.donya-e-eqtesad.com/fa/tags/%D8%A8%D9%88%D8%AF%D8%AC%D9%87\">بودجه</a></strong>&nbsp;اداره&nbsp;<strong><a title=\"پایتخت\" href=\"https://www.donya-e-eqtesad.com/fa/tags/%D9%BE%D8%A7%DB%8C%D8%AA%D8%AE%D8%AA\">پایتخت</a></strong>&nbsp;در سال جاری، رقمی معادل ۳ هزار و ۸۰۰ میلیارد تومان از محل منابع غیر نقد شامل دو جزء اصلی &laquo;فروش ملک&raquo; و &laquo;صدور جواز ساخت بدون دریافت&nbsp;<strong><a title=\"پول\" href=\"https://www.donya-e-eqtesad.com/fa/tags/%D9%BE%D9%88%D9%84\">پول</a></strong>نقد(هولوگرام)&raquo; تامین می&zwnj;شود. از این رو&nbsp;<strong><a title=\"شهرداری\" href=\"https://www.donya-e-eqtesad.com/fa/tags/%D8%B4%D9%87%D8%B1%D8%AF%D8%A7%D8%B1%DB%8C\">شهرداری</a></strong>&nbsp;برای تامین بخشی از منابع غیرنقد خود،&nbsp;<strong><a href=\"https://static1.donya-e-eqtesad.com/servev2/mrDU8yLQRLAI/VpZsibB36zwuV2YKDk6btsEcAi-iwmoSS3tNnLMSGeY,/%DA%AF%D8%B2%D8%A7%D8%B1%D8%B4+%D8%B9%D9%85%D9%84%DA%A9%D8%B1%D8%AF+9+%D9%85%D8%A7%D9%87%D9%87+%D8%B4%D9%87%D8%B1%D8%AF%D8%A7%D8%B1%DB%8C+%D8%AA%D9%87%D8%B1%D8%A7%D9%86.pdf\" target=\"_blank\" rel=\"noopener\">لیست اموال قابل فروش برای سال ۹۸</a></strong>&nbsp;را اعلام عمومی کرده است. این لیست مجموعه متنوعی از املاک و مستغلاتی است که مالکیت آن به لحاظ ثبتی و رسمی در اختیار<strong><a title=\"شهرداری تهران\" href=\"https://www.donya-e-eqtesad.com/fa/tags/%D8%B4%D9%87%D8%B1%D8%AF%D8%A7%D8%B1%DB%8C_%D8%AA%D9%87%D8%B1%D8%A7%D9%86\">شهرداری تهران</a></strong>&nbsp;قرار دارد و دارای دو خصوصیت مرئی و نامرئی است.</p>\r\n<p dir=\"rtl\">خصوصیت مرئی املاک ثبت شده در لیست اموال قابل فروش شهرداری، به موقعیت مکانی و جغرافیایی این املاک در محدوده<strong><a title=\"شهر تهران\" href=\"https://www.donya-e-eqtesad.com/fa/tags/%D8%B4%D9%87%D8%B1_%D8%AA%D9%87%D8%B1%D8%A7%D9%86\">شهر تهران</a></strong>&nbsp;باز می&zwnj;گردد. جزئیات ۵۹۵ فقره املاک و مستغلات قابل فروش نشان می&zwnj;دهد: ۹۸ درصد این املاک در نیمه جنوبی پایتخت و بیش از ۵۰ درصد آن در جنوبی&zwnj;ترین نقطه حریم پایتخت قرار گرفته&zwnj;اند. دیگر خصوصیت املاک قرار گرفته در این لیست که نامرئی است مربوط به درجه و نوع مرغوبیت این املاک است. بررسی&zwnj;ها حاکی از آن است که این املاک در گروه املاک فوق مرغوب شهر محسوب نمی&zwnj;شوند. به تعبیری دیگر در قالب این خصوصیت می&zwnj;توان گفت عمده املاک ارائه شده در این لیست املاکی هستند که از نظر مدیریت شهری پایتخت امکان بهره&zwnj;برداری و انتفاع حداکثری از آنها برای مدیریت شهری وجود ندارد که البته این موضوع به معنای غیرقابل استفاده بودن این املاک برای سایر بخش&zwnj;ها از جمله&nbsp;<strong><a title=\"بخش خصوصی\" href=\"https://www.donya-e-eqtesad.com/fa/tags/%D8%A8%D8%AE%D8%B4_%D8%AE%D8%B5%D9%88%D8%B5%DB%8C\">بخش خصوصی</a></strong>&nbsp;نیست. شکل فروش املاک معرفی شده از سوی شهرداری تهران، مزایده&zwnj;ای است و شامل انواع زمین، پارکینگ، پاساژ، ساختمان اداری و تعداد معدودی آپارتمان مسکونی است. نکته جالب آنکه ۵۹۵ فقره ملک معرفی شده در این لیست حدود ۱۰ درصد از کل موجودی اعلام شده از املاک قابل فروش تحت مالکیت شهرداری تهران است.</p>\r\n<p dir=\"rtl\">طبق آخرین گزارش رسمی شهرداری مربوط به ابتدای سال ۹۷، موجودی املاک قابل فروش و غیرقابل فروش(همچون ساختمان&zwnj;های اداری) تحت مالکیت شهرداری تهران بیش از ۶۰ هزار فقره هستند که از این میان ۶ هزار و ۲۹۸ فقره در گروه املاک قابل فروش قرار دارند. از این تعداد، ۲۵ درصد املاک معادل یک هزار و ۵۵۲ فقره دارای کاربری مسکونی، ۷/ ۷ درصد املاک معادل ۴۸۶ فقره دارای کاربری اداری، ۲۰ درصد املاک معادل یک هزار و ۲۷۸ فقره دارای کاربری تجاری و ۱۳ درصد آنها معادل ۸۱۸ قطعه،&nbsp;<strong><a title=\"زمین\" href=\"https://www.donya-e-eqtesad.com/fa/tags/%D8%B2%D9%85%DB%8C%D9%86\">زمین</a></strong>&nbsp;است. مطابق با برآوردهای&nbsp;<strong><a title=\"کارشناسی\" href=\"https://www.donya-e-eqtesad.com/fa/tags/%DA%A9%D8%A7%D8%B1%D8%B4%D9%86%D8%A7%D8%B3%DB%8C\">کارشناسی</a></strong>&nbsp;انجام شده مجموع ارزش ۵۹۵ ملک ارائه شده در این لیست معادل ۶ هزار و ۶۷۵ میلیارد تومان است که علی&zwnj;القاعده باید به شکل&nbsp;<strong><a title=\"مزایده\" href=\"https://www.donya-e-eqtesad.com/fa/tags/%D9%85%D8%B2%D8%A7%DB%8C%D8%AF%D9%87\">مزایده</a></strong>&nbsp;به فروش برسد. در این لیست نیز ۴۲ درصد املاک کاربری تجاری، ۲۹ درصد کاربری پارکینگ، ۱۰ درصد زمین و ۳ درصد آپارتمان با کاربری مسکونی هستند.</p>\r\n<p dir=\"rtl\">در لیستی که هم&zwnj;اکنون به&zwnj;صورت فایل پی&zwnj;دی&zwnj;اف از سوی شهرداری منتشر شده و &laquo;دنیای اقتصاد&raquo; آن را در سایت خود به&zwnj;صورت همزمان با انتشار این گزارش بارگذاری می&zwnj;کند اگرچه هنوز به&zwnj;صورت آنلاین قابل رویت نیست؛ موقعیت جغرافیایی این املاک قابل توجه است چراکه بخش عمده یعنی ۹۸ درصد املاک در نیمه جنوبی شهر واقع شده&zwnj; و برای فروش عرضه شده&zwnj;اند. از این رو این سوال ایجاد می&zwnj;شود که آیا شهرداری در مناطق شمالی شهر(یعنی مناطق یک تا پنج) املاک تحت اختیار ندارد و اگر دارد چرا اسامی آنها در این لیست ارائه نشده است؟ در پاسخ به این سوال می&zwnj;توان گفت از آنجاکه بخش عمده املاک متعلق به شهرداری واقع در مناطق شمالی، جزو املاک فوق مرغوب(به لحاظ کیفیت و ارزش) محسوب می&zwnj;شوند شهرداری علاقه&zwnj;ای به فروش و عرضه آنها ندارد. هرچند که جست&zwnj;و&zwnj;جو در میان املاک احتمالی تحت مالکیت شهرداری این موضوع را روشن می&zwnj;کند که به دلیل نبود سامانه جامع املاک، مدیران شهرداری از موقعیت و کم وکیف برخی املاک متعلق به شهرداری خبر ندارند.</p>\r\n<h2 dir=\"rtl\">فروش املاک از مسیر جدید</h2>\r\n<p dir=\"rtl\">بررسی&zwnj;ها نشان می&zwnj;دهد تامین منابع برای اداره شهر تهران از محل فروش ملک سابقه تقریبا طولانی در مجموعه مدیریت شهری پایتخت دارد. طی سال&zwnj;های گذشته به ویژه در دوره مدیریت شهری گذشته و در اوج رکود ساختمانی از یکسو کسری منابع حاصل از کاهش صدور پروانه ساختمانی و از سوی دیگر اجرای ابرپروژه&zwnj;های شهری پول&zwnj;خوار(ساخت بزرگراه&zwnj;ها و تونل&zwnj;های شهری برای جولان خودروهای شخصی) سبب شد تا شهرداری به حرکت در ساده&zwnj;ترین مسیر تامین مالی یعنی فروش دارایی&zwnj;های ارزشمند شهر به شکل تهاتر با هزینه پیمانکاران ابر&zwnj;پروژه&zwnj;ها هدایت شود.</p>', 0, '6,', 1, 1, '2019-04-14 02:22:03', '2019-04-14 02:31:11', '2019-04-14 02:31:11'),
